patch:
diff --git a/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp b/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
index 6027572f1e84..3c7422373696 100644
--- a/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
+++ b/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
@@ -2125,9 +2125,16 @@ void VPScalarIVStepsRecipe::execute(VPTransformState &State) {
     if (getUnrollPart(*this) != 1) {
       StartIdx0 =
           Builder.CreateMul(StartIdx0, ConstantInt::get(StartIdx0->getType(),
-                                                        getUnrollPart(*this)));
+                                                         getUnrollPart(*this)));
+    }
+    // Add type compatibility check before truncation to prevent assertion failure
+    if (StartIdx0->getType()->isIntegerTy() && IntStepTy->isIntegerTy() &&
+        StartIdx0->getType()->getIntegerBitWidth() > IntStepTy->getIntegerBitWidth()) {
+      StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
+    } else if (StartIdx0->getType() != IntStepTy) {
+      // If types don't match but truncation is not valid, use a cast operation
+      StartIdx0 = Builder.CreateIntCast(StartIdx0, IntStepTy, true);
     }
-    StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
   }

   if (!FirstLaneOnly && State.VF.isScalable()) {
