patch:
diff --git a/clang/lib/CodeGen/CodeGenTypes.cpp b/clang/lib/CodeGen/CodeGenTypes.cpp
index b94c11802a26..e39cb997b2e1 100644
--- a/clang/lib/CodeGen/CodeGenTypes.cpp
+++ b/clang/lib/CodeGen/CodeGenTypes.cpp
@@ -849,8 +849,12 @@ CodeGenTypes::getCGRecordLayout(const RecordDecl *RD) {
   return *I->second;
 }

-bool CodeGenTypes::isPointerZeroInitializable(QualType T) {
-  assert((T->isAnyPointerType() || T->isBlockPointerType()) && "Invalid type");
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
+ bool CodeGenTypes::isPointerZeroInitializable(QualType T) {
+  // Check if T is a valid pointer or block pointer type before proceeding
+  if (!(T->isAnyPointerType() || T->isBlockPointerType())) {
+    return false;
+  }
   return isZeroInitializable(T);
 }
