patch:
diff --git a/clang/lib/CodeGen/CGExprAgg.cpp b/clang/lib/CodeGen/CGExprAgg.cpp
index 87b2a73fb0c0..b125e0e32224 100644
--- a/clang/lib/CodeGen/CGExprAgg.cpp
+++ b/clang/lib/CodeGen/CGExprAgg.cpp
@@ -1663,7 +1663,7 @@ static bool isSimpleZero(const Expr *E, CodeGenFunction &CGF) {

   // 0
   if (const IntegerLiteral *IL = dyn_cast<IntegerLiteral>(E))
-    return IL->getValue() == 0;
+return IL->getValue() == 0;
   // +0.0
   if (const FloatingLiteral *FL = dyn_cast<FloatingLiteral>(E))
     return FL->getValue().isPosZero();
@@ -1671,11 +1671,15 @@ static bool isSimpleZero(const Expr *E, CodeGenFunction &CGF) {
   if ((isa<ImplicitValueInitExpr>(E) || isa<CXXScalarValueInitExpr>(E)) &&
       CGF.getTypes().isZeroInitializable(E->getType()))
     return true;
-  // (int*)0 - Null pointer expressions.
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
+   // (int*)0 - Null pointer expressions.
   if (const CastExpr *ICE = dyn_cast<CastExpr>(E))
-    return ICE->getCastKind() == CK_NullToPointer &&
-           CGF.getTypes().isPointerZeroInitializable(E->getType()) &&
-           !E->HasSideEffects(CGF.getContext());
+    if (E->getType()->isPointerType() || E->getType()->isBlockPointerType())
+      return ICE->getCastKind() == CK_NullToPointer &&
+             CGF.getTypes().isPointerZeroInitializable(E->getType()) &&
+             !E->HasSideEffects(CGF.getContext());
+    else
+      return false;
   // '\0'
   if (const CharacterLiteral *CL = dyn_cast<CharacterLiteral>(E))
     return CL->getValue() == 0;
@@ -1683,8 +1687,6 @@ static bool isSimpleZero(const Expr *E, CodeGenFunction &CGF) {
   // Otherwise, hard case: conservatively return false.
   return false;
 }
-
-
 void
 AggExprEmitter::EmitInitializationToLValue(Expr *E, LValue LV) {
   QualType type = LV.getType();
