patch:
diff --git a/clang/lib/CodeGen/CGExprAgg.cpp b/clang/lib/CodeGen/CGExprAgg.cpp
index 87b2a73fb0c0..50beafdeaf8d 100644
--- a/clang/lib/CodeGen/CGExprAgg.cpp
+++ b/clang/lib/CodeGen/CGExprAgg.cpp
@@ -1671,9 +1671,11 @@ static bool isSimpleZero(const Expr *E, CodeGenFunction &CGF) {
   if ((isa<ImplicitValueInitExpr>(E) || isa<CXXScalarValueInitExpr>(E)) &&
       CGF.getTypes().isZeroInitializable(E->getType()))
     return true;
-  // (int*)0 - Null pointer expressions.
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
+   // (int*)0 - Null pointer expressions.
   if (const CastExpr *ICE = dyn_cast<CastExpr>(E))
     return ICE->getCastKind() == CK_NullToPointer &&
+           (E->getType()->isPointerType() || E->getType()->isBlockPointerType()) &&
            CGF.getTypes().isPointerZeroInitializable(E->getType()) &&
            !E->HasSideEffects(CGF.getContext());
   // '\0'
@@ -1684,7 +1686,6 @@ static bool isSimpleZero(const Expr *E, CodeGenFunction &CGF) {
   return false;
 }

