diff --git a/mlir/lib/IR/Block.cpp b/mlir/lib/IR/Block.cpp
index 959a60c04450..2322594a707f 100644
--- a/mlir/lib/IR/Block.cpp
+++ b/mlir/lib/IR/Block.cpp
@@ -244,7 +244,8 @@ void Block::eraseArguments(function_ref<bool(BlockArgument)> shouldEraseFn) {
 /// Get the terminator operation of this block. This function asserts that
 /// the block might have a valid terminator operation.
 Operation *Block::getTerminator() {
-  assert(mightHaveTerminator());
+  if (!mightHaveTerminator())
+    return nullptr;
   return &back();
 }
