patch:
diff --git a/mlir/lib/IR/Block.cpp b/mlir/lib/IR/Block.cpp
index 959a60c04450..241632c39923 100644
--- a/mlir/lib/IR/Block.cpp
+++ b/mlir/lib/IR/Block.cpp
@@ -241,10 +241,11 @@ void Block::eraseArguments(function_ref<bool(BlockArgument)> shouldEraseFn) {
 // Terminator management
 //===----------------------------------------------------------------------===//

-/// Get the terminator operation of this block. This function asserts that
-/// the block might have a valid terminator operation.
+/// Get the terminator operation of this block. This function now returns nullptr if the block does not have a valid terminator operation.
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
 Operation *Block::getTerminator() {
-  assert(mightHaveTerminator());
+  if (!mightHaveTerminator())
+    return nullptr;
   return &back();
 }
