patch:
diff --git a/mlir/lib/IR/Block.cpp b/mlir/lib/IR/Block.cpp
index 959a60c04450..aa3bb03e4c73 100644
--- a/mlir/lib/IR/Block.cpp
+++ b/mlir/lib/IR/Block.cpp
@@ -241,10 +241,12 @@ void Block::eraseArguments(function_ref<bool(BlockArgument)> shouldEraseFn) {
 // Terminator management
 //===----------------------------------------------------------------------===//

-/// Get the terminator operation of this block. This function asserts that
-/// the block might have a valid terminator operation.
+/// Get the terminator operation of this block. This function returns nullptr
+/// if the block does not have a valid terminator operation.
+// FIXED: Do not assert, return nullptr if precondition fails
 Operation *Block::getTerminator() {
-  assert(mightHaveTerminator());
+  if (!mightHaveTerminator())
+    return nullptr;
   return &back();
 }
