patch:
diff --git a/clang/lib/Parse/ParseStmt.cpp b/clang/lib/Parse/ParseStmt.cpp
index bdd02469a85b..39d685fc88d2 100644
--- a/clang/lib/Parse/ParseStmt.cpp
+++ b/clang/lib/Parse/ParseStmt.cpp
@@ -126,10 +126,14 @@ Parser::ParseStatementOrDeclaration(StmtVector &Stmts,
       Stmts, StmtCtx, TrailingElseLoc, CXX11Attrs, GNUOrMSAttrs);
   MaybeDestroyTemplateIds();

-  takeAndConcatenateAttrs(CXX11Attrs, std::move(GNUOrMSAttrs));
+// FIXME: Crash CWE-617: Reachable Assertion
+   takeAndConcatenateAttrs(CXX11Attrs, std::move(GNUOrMSAttrs));

-  assert((CXX11Attrs.empty() || Res.isInvalid() || Res.isUsable()) &&
-         "attributes on empty statement");
+  // Convert assertion to a recoverable error condition by clearing attributes
+  // when result is neither invalid nor usable
+  if (!CXX11Attrs.empty() && !Res.isInvalid() && !Res.isUsable()) {
+    CXX11Attrs.clear();
+  }

   if (CXX11Attrs.empty() || Res.isInvalid())
     return Res;
