patch:
diff --git a/clang/lib/Parse/ParseStmt.cpp b/clang/lib/Parse/ParseStmt.cpp
index bdd02469a85b..05581c7bd38b 100644
--- a/clang/lib/Parse/ParseStmt.cpp
+++ b/clang/lib/Parse/ParseStmt.cpp
@@ -128,8 +128,16 @@ Parser::ParseStatementOrDeclaration(StmtVector &Stmts,

   takeAndConcatenateAttrs(CXX11Attrs, std::move(GNUOrMSAttrs));

+  // Instead of assert, explicitly check and recover if the statement is not usable but has attributes.
+  if (!CXX11Attrs.empty() && !(Res.isInvalid() || Res.isUsable())) {
+    Diag(CXX11Attrs.Range.getBegin(), diag::err_expected_statement);
+    CXX11Attrs.clear();
+    return StmtError();
+  }
+
   assert((CXX11Attrs.empty() || Res.isInvalid() || Res.isUsable()) &&
-         "attributes on empty statement");
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
+          "attributes on empty statement");

   if (CXX11Attrs.empty() || Res.isInvalid())
     return Res;
