patch:
diff --git a/llvm/lib/CodeGen/SelectionDAG/SelectionDAG.cpp b/llvm/lib/CodeGen/SelectionDAG/SelectionDAG.cpp
index bbf1b0fd590e..e3efce8bb6a2 100644
--- a/llvm/lib/CodeGen/SelectionDAG/SelectionDAG.cpp
+++ b/llvm/lib/CodeGen/SelectionDAG/SelectionDAG.cpp
@@ -10402,9 +10402,16 @@ SDValue SelectionDAG::getNode(unsigned Opcode, const SDLoc &DL, EVT VT,
   unsigned NumOps = Ops.size();
   switch (NumOps) {
   case 0: return getNode(Opcode, DL, VT);
-  case 1: return getNode(Opcode, DL, VT, Ops[0], Flags);
-  case 2: return getNode(Opcode, DL, VT, Ops[0], Ops[1], Flags);
-  case 3: return getNode(Opcode, DL, VT, Ops[0], Ops[1], Ops[2], Flags);
+// FIXME: Crash CWE-825: Expired Pointer Dereference
+   case 1: if (Ops[0].getOpcode() == ISD::DELETED_NODE)
+             return SDValue();
+           return getNode(Opcode, DL, VT, Ops[0], Flags);
+  case 2: if (Ops[0].getOpcode() == ISD::DELETED_NODE || Ops[1].getOpcode() == ISD::DELETED_NODE)
+             return SDValue();
+           return getNode(Opcode, DL, VT, Ops[0], Ops[1], Flags);
+  case 3: if (Ops[0].getOpcode() == ISD::DELETED_NODE || Ops[1].getOpcode() == ISD::DELETED_NODE || Ops[2].getOpcode() == ISD::DELETED_NODE)
+             return SDValue();
+           return getNode(Opcode, DL, VT, Ops[0], Ops[1], Ops[2], Flags);
   default: break;
   }
