diff --git a/clang/lib/Sema/SemaOpenMP.cpp b/clang/lib/Sema/SemaOpenMP.cpp
index 835dba22a858..7fe0629ad969 100644
--- a/clang/lib/Sema/SemaOpenMP.cpp
+++ b/clang/lib/Sema/SemaOpenMP.cpp
@@ -14346,9 +14346,19 @@ StmtResult SemaOpenMP::ActOnOpenMPTileDirective(ArrayRef<OMPClause *> Clauses,
     // Build expr:
     // \code{c}
     //   (TS <= 0) ? 1 : TS
-    // \endcode
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
+     // \endcode
     QualType DimTy = DimTileSizeExpr->getType();
-    uint64_t DimWidth = Context.getTypeSize(DimTy);
+    // Check if we can safely get the type size, otherwise use a safe default
+    uint64_t DimWidth;
+    if (DimTy.isNull() || DimTy->isIncompleteType() || DimTy->isVoidType() || 
+        !DimTy->isIntegerType()) {
+      // Fallback to standard integer type for unknown/invalid types
+      DimTy = Context.IntTy;
+      DimWidth = Context.getTypeSize(Context.IntTy);
+    } else {
+      DimWidth = Context.getTypeSize(DimTy);
+    }
     IntegerLiteral *Zero = IntegerLiteral::Create(
         Context, llvm::APInt::getZero(DimWidth), DimTy, {});
     IntegerLiteral *One =
