patch:
diff --git a/src/string.c b/src/string.c
index 1bd3f6f8f..28b56f637 100644
--- a/src/string.c
+++ b/src/string.c
@@ -2948,7 +2948,15 @@ str_bytesplice(mrb_state *mrb, mrb_value str, mrb_int idx1, mrb_int len1, mrb_va
 {
   struct RString *s = RSTRING(str);
   if (RSTR_LEN(s) < idx1 || RSTRING_LEN(replace) < idx2) {
-    mrb_raise(mrb, E_INDEX_ERROR, "index out of string");
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+     mrb_raise(mrb, E_INDEX_ERROR, "index out of string");
+  }
+  // Check for integer overflow in idx1+len1 and idx2+len2
+  if (len1 > 0 && idx1 > MRB_INT_MAX - len1) {
+    mrb_raise(mrb, E_INDEX_ERROR, "integer overflow on idx1+len1");
+  }
+  if (len2 > 0 && idx2 > MRB_INT_MAX - len2) {
+    mrb_raise(mrb, E_INDEX_ERROR, "integer overflow on idx2+len2");
   }
   if (RSTR_LEN(s) <= idx1+len1) {
     len1 = RSTR_LEN(s) - idx1;
@@ -2961,13 +2969,35 @@ str_bytesplice(mrb_state *mrb, mrb_value str, mrb_int idx1, mrb_int len1, mrb_va
   if (len1 >= len2) {
     memmove(RSTR_PTR(s)+idx1, RSTRING_PTR(replace)+idx2, len2);
     if (len1 > len2) {
+      if (idx1 > MRB_INT_MAX - len2) {
+        mrb_raise(mrb, E_INDEX_ERROR, "integer overflow on idx1+len2");
+      }
+      if (idx1 > MRB_INT_MAX - len1) {
+        mrb_raise(mrb, E_INDEX_ERROR, "integer overflow on idx1+len1");
+      }
+      if ((RSTR_LEN(s) < (idx1+len1))) {
+        mrb_raise(mrb, E_INDEX_ERROR, "overflow in memmove src/dst");
+      }
       memmove(RSTR_PTR(s)+idx1+len2, RSTR_PTR(s)+idx1+len1, RSTR_LEN(s)-(idx1+len1));
       RSTR_SET_LEN(s, RSTR_LEN(s)-(len1-len2));
     }
   }
   else { /* len1 < len2 */
     mrb_int slen = RSTR_LEN(s);
+    // Check for overflow in slen+len2-len1
+    if (len2 > len1 && slen > MRB_INT_MAX - (len2-len1)) {
+      mrb_raise(mrb, E_INDEX_ERROR, "integer overflow on resize");
+    }
     mrb_str_resize(mrb, str, slen+len2-len1);
+    if (idx1 > MRB_INT_MAX - len2) {
+      mrb_raise(mrb, E_INDEX_ERROR, "integer overflow on idx1+len2");
+    }
+    if (idx1 > MRB_INT_MAX - len1) {
+      mrb_raise(mrb, E_INDEX_ERROR, "integer overflow on idx1+len1");
+    }
+    if ((slen < (idx1+len1))) {
+      mrb_raise(mrb, E_INDEX_ERROR, "overflow in memmove src/dst");
+    }
     memmove(RSTR_PTR(s)+idx1+len2, RSTR_PTR(s)+idx1+len1, slen-(idx1+len1));
     memmove(RSTR_PTR(s)+idx1, RSTRING_PTR(replace)+idx2, len2);
   }
