diff --git a/ext/simplexml/simplexml.c b/ext/simplexml/simplexml.c
index ae3c98b7e3e..51dd2a068f0 100644
--- a/ext/simplexml/simplexml.c
+++ b/ext/simplexml/simplexml.c
@@ -419,7 +419,15 @@ static zval *sxe_prop_dim_write(zend_object *object, zval *member, zval *value,
 		}
 	}

-	GET_NODE(sxe, node);
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+ 	GET_NODE(sxe, node);
+
+	if (!node) {
+		if (member == &tmp_zv) {
+			zval_ptr_dtor_str(&tmp_zv);
+		}
+		return &EG(error_zval);
+	}

 	php_libxml_invalidate_node_list_cache_from_doc(node->doc);

@@ -431,8 +439,7 @@ static zval *sxe_prop_dim_write(zend_object *object, zval *member, zval *value,
 		test = sxe->iter.name != NULL;
 	} else if (sxe->iter.type != SXE_ITER_CHILD) {
 		mynode = node;
-		node = php_sxe_get_first_node(sxe, node);
-		attr = node ? node->properties : NULL;
+		node = php_sxe_get_first_node(sxe, node);		attr = node ? node->properties : NULL;
 		test = 0;
 		if (!member && node && node->parent &&
 		    node->parent->type == XML_DOCUMENT_NODE) {
