patch:
diff --git a/ext/standard/array.c b/ext/standard/array.c
index eee260f2243..d6607be1134 100644
--- a/ext/standard/array.c
+++ b/ext/standard/array.c
@@ -2958,8 +2958,19 @@ PHP_FUNCTION(range)
 			start_type = IS_LONG;
 			goto handle_numeric_inputs;
 		}
-
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+ 
 		/* Generate array of characters */
+		/* Validate string parameters before accessing their values */
+		if (!user_start || Z_TYPE_P(user_start) != IS_STRING || !Z_STRVAL_P(user_start) || Z_STRLEN_P(user_start) < 1) {
+			php_error_docref(NULL, E_WARNING, "Invalid start parameter for character range");
+			RETURN_FALSE;
+		}
+		if (!user_end || Z_TYPE_P(user_end) != IS_STRING || !Z_STRVAL_P(user_end) || Z_STRLEN_P(user_end) < 1) {
+			php_error_docref(NULL, E_WARNING, "Invalid end parameter for character range");
+			RETURN_FALSE;
+		}
+		
 		unsigned char low = (unsigned char)Z_STRVAL_P(user_start)[0];
 		unsigned char high = (unsigned char)Z_STRVAL_P(user_end)[0];
