patch:
diff --git a/ext/standard/array.c b/ext/standard/array.c
index eee260f2243..9417215d0df 100644
--- a/ext/standard/array.c
+++ b/ext/standard/array.c
@@ -2958,7 +2958,17 @@ PHP_FUNCTION(range)
 			start_type = IS_LONG;
 			goto handle_numeric_inputs;
 		}
-
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+
+		/* Validate string parameters before character extraction */
+		if (UNEXPECTED(!user_start || !user_end || 
+			Z_TYPE_P(user_start) != IS_STRING || Z_TYPE_P(user_end) != IS_STRING ||
+			Z_STRLEN_P(user_start) == 0 || Z_STRLEN_P(user_end) == 0 ||
+			!Z_STRVAL_P(user_start) || !Z_STRVAL_P(user_end))) {
+			php_error_docref(NULL, E_WARNING, "Invalid string parameters for character range");
+			RETURN_FALSE;
+		}
+ 
 		/* Generate array of characters */
 		unsigned char low = (unsigned char)Z_STRVAL_P(user_start)[0];
 		unsigned char high = (unsigned char)Z_STRVAL_P(user_end)[0];
