patch:
diff --git a/ext/standard/array.c b/ext/standard/array.c
index eee260f2243..027d60d3278 100644
--- a/ext/standard/array.c
+++ b/ext/standard/array.c
@@ -2959,6 +2959,14 @@ PHP_FUNCTION(range)
 			goto handle_numeric_inputs;
 		}

+		/* Comprehensive validation of string arguments to prevent use-after-free */
+		if (UNEXPECTED(Z_TYPE_P(user_start) != IS_STRING || Z_TYPE_P(user_end) != IS_STRING ||
+			Z_STRVAL_P(user_start) == NULL || Z_STRVAL_P(user_end) == NULL ||
+			Z_STRLEN_P(user_start) < 1 || Z_STRLEN_P(user_end) < 1)) {
+			php_error_docref(NULL, E_WARNING, "String arguments must be valid non-empty strings");
+			RETURN_FALSE;
+		}
+ 
 		/* Generate array of characters */
 		unsigned char low = (unsigned char)Z_STRVAL_P(user_start)[0];
 		unsigned char high = (unsigned char)Z_STRVAL_P(user_end)[0];
