patch:
diff --git a/ext/standard/array.c b/ext/standard/array.c
index eee260f2243..a4fbef55e05 100644
--- a/ext/standard/array.c
+++ b/ext/standard/array.c
@@ -2958,8 +2958,15 @@ PHP_FUNCTION(range)
 			start_type = IS_LONG;
 			goto handle_numeric_inputs;
 		}
-
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+ 
 		/* Generate array of characters */
+		/* Validate input parameters to prevent NULL pointer dereference */
+		if (Z_TYPE_P(user_start) != IS_STRING || Z_STRVAL_P(user_start) == NULL || Z_STRLEN_P(user_start) == 0 ||
+		    Z_TYPE_P(user_end) != IS_STRING || Z_STRVAL_P(user_end) == NULL || Z_STRLEN_P(user_end) == 0) {
+			php_error_docref(NULL, E_WARNING, "Invalid string arguments provided to range()");
+			RETURN_FALSE;
+		}
 		unsigned char low = (unsigned char)Z_STRVAL_P(user_start)[0];
 		unsigned char high = (unsigned char)Z_STRVAL_P(user_end)[0];
