diff --git a/ext/standard/array.c b/ext/standard/array.c
index eee260f2243..d4e624a85f5 100644
--- a/ext/standard/array.c
+++ b/ext/standard/array.c
@@ -2958,6 +2958,13 @@ PHP_FUNCTION(range)
 			start_type = IS_LONG;
 			goto handle_numeric_inputs;
 		}
+// FIXME: Crash CWE-824: Access of Uninitialized Pointer
+		/* Added bound and initialization check before char extraction */
+		if (!user_start || Z_TYPE_P(user_start) != IS_STRING || Z_STRLEN_P(user_start) < 1 ||
+		    !user_end || Z_TYPE_P(user_end) != IS_STRING || Z_STRLEN_P(user_end) < 1) {
+			php_error_docref(NULL, E_WARNING, "Both $start and $end must be non-empty strings");
+			RETURN_FALSE;
+		}

 		/* Generate array of characters */
 		unsigned char low = (unsigned char)Z_STRVAL_P(user_start)[0];
