patch:
diff --git a/ext/standard/array.c b/ext/standard/array.c
index eee260f2243..90db7adcaf0 100644
--- a/ext/standard/array.c
+++ b/ext/standard/array.c
@@ -2958,6 +2958,13 @@ PHP_FUNCTION(range)
 			start_type = IS_LONG;
 			goto handle_numeric_inputs;
 		}
+// FIXME: Crash CWE-824: Access of Uninitialized Pointer
+
+		/* Defensive check: Ensure user_start and user_end are strings and not NULL, and length is at least 1 */
+		if (Z_TYPE_P(user_start) != IS_STRING || Z_TYPE_P(user_end) != IS_STRING || Z_STRLEN_P(user_start) < 1 || Z_STRLEN_P(user_end) < 1) {
+			php_error_docref(NULL, E_WARNING, "Arguments #1 ($start) and #2 ($end) must both be non-empty single byte strings");
+			RETURN_FALSE;
+		}

 		/* Generate array of characters */
 		unsigned char low = (unsigned char)Z_STRVAL_P(user_start)[0];
