+		return;
+	}
+	elements = &element->watch->elements;
 	phpdbg_unwatch_parent_ht(element);
 	zend_hash_del(elements, element->str);
 	if (zend_hash_num_elements(elements) == 0) {
@@ -980,8 +985,7 @@ void phpdbg_clean_watch_element(phpdbg_watch_element *element) {
