patch:
diff --git a/Zend/zend_opcode.c b/Zend/zend_opcode.c
index 97110cf54c0..89f5516961c 100644
--- a/Zend/zend_opcode.c
+++ b/Zend/zend_opcode.c
@@ -1071,6 +1071,10 @@ ZEND_API void pass_two(zend_op_array *op_array)
 				}
 				break;
 			case ZEND_FAST_CALL:
+				if (UNEXPECTED(op_array->try_catch_array == NULL)) {
+					zend_error(E_ERROR, "Missing try_catch_array during opcode compilation");
+					return;
+				}
 				opline->op1.opline_num = op_array->try_catch_array[opline->op1.num].finally_op;
 				ZEND_PASS_TWO_UPDATE_JMP_TARGET(op_array, opline, opline->op1);
 				break;
