patch:
diff --git a/ext/pdo_sqlite/sqlite_driver.c b/ext/pdo_sqlite/sqlite_driver.c
index 2f494c2ddb8..bfc249e5776 100644
--- a/ext/pdo_sqlite/sqlite_driver.c
+++ b/ext/pdo_sqlite/sqlite_driver.c
@@ -439,12 +439,17 @@ static int do_callback(struct pdo_sqlite_fci *fc, zval *cb,
 	} else {
 		/* we're stepping in an aggregate; the return value goes into
 		 * the context */
-		if (agg_context) {
+// FIXME: Crash CWE-416: Use After Free
+ 		if (agg_context) {
 			if (Z_ISUNDEF(retval)) {
-				zval_ptr_dtor(&agg_context->val);
+				if (Z_REFCOUNTED(agg_context->val) && Z_REFCOUNT(agg_context->val) > 1) {
+					zval_ptr_dtor(&agg_context->val);
+				}
 				return FAILURE;
 			}
-			zval_ptr_dtor(Z_REFVAL(agg_context->val));
+			if (Z_REFCOUNTED_P(Z_REFVAL(agg_context->val)) && Z_REFCOUNT_P(Z_REFVAL(agg_context->val)) > 1) {
+				zval_ptr_dtor(Z_REFVAL(agg_context->val));
+			}
 			ZVAL_COPY_VALUE(Z_REFVAL(agg_context->val), &retval);
 			ZVAL_UNDEF(&retval);
 		}
