patch:
diff --git a/ext/pdo_sqlite/sqlite_driver.c b/ext/pdo_sqlite/sqlite_driver.c
index 2f494c2ddb8..7c9eb8aec6d 100644
--- a/ext/pdo_sqlite/sqlite_driver.c
+++ b/ext/pdo_sqlite/sqlite_driver.c
@@ -439,17 +439,21 @@ static int do_callback(struct pdo_sqlite_fci *fc, zval *cb,
 	} else {
 		/* we're stepping in an aggregate; the return value goes into
 		 * the context */
-		if (agg_context) {
+// FIXME: Crash CWE-416: Use After Free
+ 		if (agg_context) {
 			if (Z_ISUNDEF(retval)) {
 				zval_ptr_dtor(&agg_context->val);
+				ZVAL_UNDEF(&agg_context->val); // Explicitly mark as UNDEF after destruction
 				return FAILURE;
 			}
 			zval_ptr_dtor(Z_REFVAL(agg_context->val));
+			ZVAL_UNDEF(Z_REFVAL(agg_context->val)); // Mark as UNDEF to prevent use-after-free
 			ZVAL_COPY_VALUE(Z_REFVAL(agg_context->val), &retval);
 			ZVAL_UNDEF(&retval);
 		}
 	}

+
 	if (!Z_ISUNDEF(retval)) {
 		zval_ptr_dtor(&retval);
 	}
