patch:
diff --git a/ext/pdo_sqlite/sqlite_driver.c b/ext/pdo_sqlite/sqlite_driver.c
index 2f494c2ddb8..78c8e1b45a2 100644
--- a/ext/pdo_sqlite/sqlite_driver.c
+++ b/ext/pdo_sqlite/sqlite_driver.c
@@ -439,9 +439,11 @@ static int do_callback(struct pdo_sqlite_fci *fc, zval *cb,
 	} else {
 		/* we're stepping in an aggregate; the return value goes into
 		 * the context */
-		if (agg_context) {
+// FIXME: Crash CWE-416: Use After Free
+ 		if (agg_context) {
 			if (Z_ISUNDEF(retval)) {
 				zval_ptr_dtor(&agg_context->val);
+				ZVAL_UNDEF(&agg_context->val); // Patch: Prevent dangling pointer use
 				return FAILURE;
 			}
 			zval_ptr_dtor(Z_REFVAL(agg_context->val));
@@ -450,6 +452,7 @@ static int do_callback(struct pdo_sqlite_fci *fc, zval *cb,
 		}
 	}

+
 	if (!Z_ISUNDEF(retval)) {
 		zval_ptr_dtor(&retval);
 	}
