patch:
diff --git a/ext/dom/text.c b/ext/dom/text.c
index fe0cc8cd5a5..b832cb15c43 100644
--- a/ext/dom/text.c
+++ b/ext/dom/text.c
@@ -75,8 +75,9 @@ zend_result dom_text_whole_text_read(dom_object *obj, zval *retval)
 		node = node->prev;
 	}

-	/* concatenate all adjacent text and cdata nodes */
-	while (node && ((node->type == XML_TEXT_NODE) || (node->type == XML_CDATA_SECTION_NODE))) {
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+ 	/* concatenate all adjacent text and cdata nodes */
+	while (node && ((node->type == XML_TEXT_NODE) || (node->type == XML_CDATA_SECTION_NODE)) && node->content) {
 		smart_str_appends(&str, (const char *) node->content);
 		node = node->next;
 	}
