patch:
diff --git a/Zend/zend_vm_execute.h b/Zend/zend_vm_execute.h
index 3bc01a597fa..1144225c4e4 100644
--- a/Zend/zend_vm_execute.h
+++ b/Zend/zend_vm_execute.h
@@ -60426,6 +60426,11 @@ ZEND_API void zend_execute(zend_op_array *op_array, zval *return_value)
 	} else {
 		call_info = ZEND_CALL_TOP_CODE | ZEND_CALL_HAS_SYMBOL_TABLE | ZEND_CALL_HAS_THIS;
 	}
+	// Ensure object_or_called_scope is valid before proceeding
+	if (object_or_called_scope == NULL) {
+		/* Prevent use-after-free by early exit if object is invalid */
+		return;
+	}
 	execute_data = zend_vm_stack_push_call_frame(call_info,
 		(zend_function*)op_array, 0, object_or_called_scope);
 	if (EG(current_execute_data)) {
@@ -60434,6 +60439,7 @@ ZEND_API void zend_execute(zend_op_array *op_array, zval *return_value)
 		execute_data->symbol_table = &EG(symbol_table);
 	}
 	EX(prev_execute_data) = EG(current_execute_data);
+// FIXME: Crash CWE-416: Use After Free
 	i_init_code_execute_data(execute_data, op_array, return_value);
 	ZEND_OBSERVER_FCALL_BEGIN(execute_data);
 	zend_execute_ex(execute_data);
