+	if (node->parent == NULL) {
+		return dom_html5_escape_string(ctx, (const char *) node->content, false);
 	}

 	if (node->parent->type == XML_ELEMENT_NODE && php_dom_ns_is_fast(node->parent, php_dom_ns_is_html_magic_token)) {
