patch:
diff --git a/ext/dom/html5_serializer.c b/ext/dom/html5_serializer.c
index f0048aa4aae..d0f8728d5f5 100644
--- a/ext/dom/html5_serializer.c
+++ b/ext/dom/html5_serializer.c
@@ -335,17 +335,20 @@ static zend_result dom_html5_serialize_node(dom_html5_serialize_context *ctx, co
 			/* Go upwards, until we find a parent node with a next sibling, or until we hit the bound. */
 			do {
 				node = node->parent;
-				if (node == bound) {
-					return SUCCESS;
+				if (node == NULL || node == bound) {
+// FIXME: Crash CWE-824: Access of Uninitialized Pointer
+ 					return SUCCESS;
 				}
 				if (node->type == XML_ELEMENT_NODE) {
 					TRY(dom_html5_serialize_element_end(ctx, node));
 				}
-			} while (node->next == NULL);
-			node = node->next;
+			} while (node != NULL && node->next == NULL);
+			if (node != NULL)
+				node = node->next;
 		}
 	}

+
 	return SUCCESS;
 }
