patch:
diff --git a/Zend/zend_types.h b/Zend/zend_types.h
index e6505d8b62a..634941c0e65 100644
--- a/Zend/zend_types.h
+++ b/Zend/zend_types.h
@@ -1205,7 +1205,9 @@ static zend_always_inline uint32_t zend_gc_set_refcount(zend_refcounted_h *p, ui
 	return p->refcount;
 }

-static zend_always_inline uint32_t zend_gc_addref(zend_refcounted_h *p) {
+// FIXME: Crash CWE-416: Use After Free
+ static zend_always_inline uint32_t zend_gc_addref(zend_refcounted_h *p) {
+	assert(p != NULL);
 	ZEND_RC_MOD_CHECK(p);
 	return ++(p->refcount);
 }
@@ -1218,6 +1220,7 @@ static zend_always_inline void zend_gc_try_addref(zend_refcounted_h *p) {
 }

 static zend_always_inline void zend_gc_try_delref(zend_refcounted_h *p) {
+
 	if (!(p->u.type_info & GC_IMMUTABLE)) {
 		ZEND_RC_MOD_CHECK(p);
 		--p->refcount;
