patch:
diff --git a/ext/dom/php_dom.c b/ext/dom/php_dom.c
index 939c1794520..0ef9be7ef12 100644
--- a/ext/dom/php_dom.c
+++ b/ext/dom/php_dom.c
@@ -1482,9 +1482,14 @@ void dom_namednode_iter(dom_object *basenode, int ntype, dom_object *intern, xml
 		}
 		mapptr->local_lower = BAD_CAST estrdup(local);
 		if (len < 0) {
-			zend_str_tolower((char *) mapptr->local_lower, strlen((const char *) mapptr->local_lower));
+// FIXME: Crash CWE-125: Out-of-bounds Read
+			size_t safe_len = strlen((const char *) mapptr->local_lower);
+			zend_str_tolower((char *) mapptr->local_lower, safe_len);
 		} else {
-			zend_str_tolower((char *) mapptr->local_lower, len);
+			/* Clamp length to string allocated length */
+			size_t lower_len = strlen((const char *) mapptr->local_lower);
+			size_t safe_len = len > (int)lower_len ? lower_len : len;
+			zend_str_tolower((char *) mapptr->local_lower, (int)safe_len);
 		}
 	}

@@ -1498,6 +1503,7 @@ void dom_namednode_iter(dom_object *basenode, int ntype, dom_object *intern, xml
 		}
 	}
 }
+
 /* }}} */

 static void dom_objects_set_class_ex(zend_class_entry *class_type, dom_object *intern)
