patch:
diff --git a/ext/dom/php_dom.c b/ext/dom/php_dom.c
index 939c1794520..5d1ad8e3278 100644
--- a/ext/dom/php_dom.c
+++ b/ext/dom/php_dom.c
@@ -1480,10 +1480,15 @@ void dom_namednode_iter(dom_object *basenode, int ntype, dom_object *intern, xml
 			mapptr->local = xmlCharStrndup(local, len);
 			mapptr->free_local = true;
 		}
-		mapptr->local_lower = BAD_CAST estrdup(local);
+		mapptr->local_lower = BAD_CAST estrndup(local, local_len); // Use estrndup to limit the allocation to local_len
 		if (len < 0) {
+// FIXME: Crash CWE-125: Out-of-bounds Read
 			zend_str_tolower((char *) mapptr->local_lower, strlen((const char *) mapptr->local_lower));
 		} else {
+// Cap len to allocated size (local_len)
+			if ((size_t) len > local_len) {
+				len = (int)local_len;
+			}
 			zend_str_tolower((char *) mapptr->local_lower, len);
 		}
 	}
@@ -1498,6 +1503,7 @@ void dom_namednode_iter(dom_object *basenode, int ntype, dom_object *intern, xml
 		}
 	}
 }
+
 /* }}} */

 static void dom_objects_set_class_ex(zend_class_entry *class_type, dom_object *intern)
