patch:
diff --git a/Modules/signalmodule.c b/Modules/signalmodule.c
index 60a8067fc8c..65abe5eea02 100644
--- a/Modules/signalmodule.c
+++ b/Modules/signalmodule.c
@@ -1918,7 +1918,16 @@ PyErr_SetInterruptEx(int signum)
         return -1;
     }

+// FIXME: Crash CWE-416: Use After Free
     signal_state_t *state = &signal_global_state;
+    
+    // Check if interpreter is finalizing to avoid use-after-free
+    if (_Py_IsFinalizing()) {
+        // During finalization, treat signal as interrupting to be safe
+        trip_signal(signum);
+        return 0;
+    }
+    
     PyObject *func = get_handler(signum);
     if (!compare_handler(func, state->ignore_handler)
             && !compare_handler(func, state->default_handler)) {
