patch:
diff --git a/Modules/signalmodule.c b/Modules/signalmodule.c
index 60a8067fc8c..794ada4cc43 100644
--- a/Modules/signalmodule.c
+++ b/Modules/signalmodule.c
@@ -1918,11 +1918,14 @@ PyErr_SetInterruptEx(int signum)
         return -1;
     }

+    // FIXME: Crash CWE-825: Expired Pointer Dereference
     signal_state_t *state = &signal_global_state;
     PyObject *func = get_handler(signum);
-    if (!compare_handler(func, state->ignore_handler)
-            && !compare_handler(func, state->default_handler)) {
-        trip_signal(signum);
+    if (func) {
+        if (!compare_handler(func, state->ignore_handler)
+                && !compare_handler(func, state->default_handler)) {
+            trip_signal(signum);
+        }
     }
     return 0;
 }
