patch:
diff --git a/Objects/unicodeobject.c b/Objects/unicodeobject.c
index 4b03cc3f4da..bbf0defcbb3 100644
--- a/Objects/unicodeobject.c
+++ b/Objects/unicodeobject.c
@@ -2696,8 +2696,12 @@ unicode_fromformat_arg(_PyUnicodeWriter *writer,
                 return NULL;
         }
         else {
-            /* UTF-8 */
+             /* UTF-8 */
             const char *s = va_arg(*vargs, const char*);
+            if (s == NULL || (void*)s < (void*)4096) {
+                PyErr_SetString(PyExc_SystemError, "Invalid string pointer in format");
+                return NULL;
+            }
             if (unicode_fromformat_write_cstr(writer, s, width, precision, flags) < 0)
                 return NULL;
         }
