patch:
diff --git a/Objects/object.c b/Objects/object.c
index 275aa6713c8..624d5801448 100644
--- a/Objects/object.c
+++ b/Objects/object.c
@@ -903,31 +903,44 @@ do_richcompare(PyThreadState *tstate, PyObject *v, PyObject *w, int op)
     PyObject *res;
     int checked_reverse_op = 0;

+    Py_INCREF(v);
+    Py_INCREF(w);
+
     if (!Py_IS_TYPE(v, Py_TYPE(w)) &&
         PyType_IsSubtype(Py_TYPE(w), Py_TYPE(v)) &&
         (f = Py_TYPE(w)->tp_richcompare) != NULL) {
         checked_reverse_op = 1;
         res = (*f)(w, v, _Py_SwappedOp[op]);
-        if (res != Py_NotImplemented)
+        if (res != Py_NotImplemented) {
+            Py_DECREF(v);
+            Py_DECREF(w);
             return res;
+        }
         Py_DECREF(res);
     }
     if ((f = Py_TYPE(v)->tp_richcompare) != NULL) {
         res = (*f)(v, w, op);
-        if (res != Py_NotImplemented)
+        if (res != Py_NotImplemented) {
+            Py_DECREF(v);
+            Py_DECREF(w);
             return res;
+        }
         Py_DECREF(res);
     }
     if (!checked_reverse_op && (f = Py_TYPE(w)->tp_richcompare) != NULL) {
         res = (*f)(w, v, _Py_SwappedOp[op]);
-        if (res != Py_NotImplemented)
+        if (res != Py_NotImplemented) {
+            Py_DECREF(v);
+            Py_DECREF(w);
             return res;
+        }
         Py_DECREF(res);
     }
     /* If neither object implements it, provide a sensible default
        for == and !=, but raise an exception for ordering. */
     switch (op) {
-    case Py_EQ:
+// FIXME: Crash CWE-416: Use After Free
+     case Py_EQ:
         res = (v == w) ? Py_True : Py_False;
         break;
     case Py_NE:
@@ -939,8 +952,12 @@ do_richcompare(PyThreadState *tstate, PyObject *v, PyObject *w, int op)
                       opstrings[op],
                       Py_TYPE(v)->tp_name,
                       Py_TYPE(w)->tp_name);
+        Py_DECREF(v);
+        Py_DECREF(w);
         return NULL;
     }
+    Py_DECREF(v);
+    Py_DECREF(w);
     return Py_NewRef(res);
 }
