patch:
diff --git a/Objects/object.c b/Objects/object.c
index 275aa6713c8..20085d38ae4 100644
--- a/Objects/object.c
+++ b/Objects/object.c
@@ -959,11 +959,18 @@ PyObject_RichCompare(PyObject *v, PyObject *w, int op)
         }
         return NULL;
     }
+    Py_INCREF(v);  // Ensure v is not deallocated during comparison
+    Py_INCREF(w);  // Ensure w is not deallocated during comparison
     if (_Py_EnterRecursiveCallTstate(tstate, " in comparison")) {
+// FIXME: Crash CWE-416: Use After Free
+        Py_DECREF(v);
+        Py_DECREF(w);
         return NULL;
     }
     PyObject *res = do_richcompare(tstate, v, w, op);
     _Py_LeaveRecursiveCallTstate(tstate);
+    Py_DECREF(v);
+    Py_DECREF(w);
     return res;
 }
