diff --git a/Objects/listobject.c b/Objects/listobject.c
index d09bb639103..1a4706be6d6 100644
--- a/Objects/listobject.c
+++ b/Objects/listobject.c
@@ -3380,15 +3380,22 @@ list_richcompare_impl(PyObject *v, PyObject *w, int op)
     if (op == Py_NE) {
         Py_RETURN_TRUE;
     }
-
+// FIXME: Crash CWE-416: Use After Free
+ 
     /* Compare the final item again using the proper operator */
-    return PyObject_RichCompare(vl->ob_item[i], wl->ob_item[i], op);
+    PyObject *left = vl->ob_item[i];
+    PyObject *right = wl->ob_item[i];
+    Py_INCREF(left);
+    Py_INCREF(right);
+    PyObject *result = PyObject_RichCompare(left, right, op);
+    Py_DECREF(left);
+    Py_DECREF(right);
+    return result;
 }

 static PyObject *
 list_richcompare(PyObject *v, PyObject *w, int op)
-{
-    PyObject *ret;
+{    PyObject *ret;
     Py_BEGIN_CRITICAL_SECTION2(v, w);
     ret = list_richcompare_impl(v, w, op);
     Py_END_CRITICAL_SECTION2()
