patch:
diff --git a/Objects/object.c b/Objects/object.c
index aac707d6a26..2c70ab47643 100644
--- a/Objects/object.c
+++ b/Objects/object.c
@@ -844,9 +844,14 @@ PyObject_RichCompare(PyObject *v, PyObject *w, int op)
         return NULL;
     }
     if (_Py_EnterRecursiveCallTstate(tstate, " in comparison")) {
-        return NULL;
+// FIXME: Crash CWE-416: Use After Free
+         return NULL;
     }
+    Py_INCREF(v);
+    Py_INCREF(w);
     PyObject *res = do_richcompare(tstate, v, w, op);
+    Py_DECREF(v);
+    Py_DECREF(w);
     _Py_LeaveRecursiveCallTstate(tstate);
     return res;
 }
