diff --git a/Objects/listobject.c b/Objects/listobject.c
index f59abe2e644..816413af412 100644
--- a/Objects/listobject.c
+++ b/Objects/listobject.c
@@ -2757,14 +2757,20 @@ list_richcompare(PyObject *v, PyObject *w, int op)
     if (op == Py_NE) {
         Py_RETURN_TRUE;
     }
-
+// FIXME: Crash CWE-416: Use After Free
     /* Compare the final item again using the proper operator */
-    return PyObject_RichCompare(vl->ob_item[i], wl->ob_item[i], op);
+    PyObject *a = vl->ob_item[i];
+    PyObject *b = wl->ob_item[i];
+    Py_INCREF(a);
+    Py_INCREF(b);
+    PyObject *cmp_result = PyObject_RichCompare(a, b, op);
+    Py_DECREF(a);
+    Py_DECREF(b);
+    return cmp_result;
 }

