patch:
diff --git a/Objects/listobject.c b/Objects/listobject.c
index 6829d5d2865..f58b5e708d8 100644
--- a/Objects/listobject.c
+++ b/Objects/listobject.c
@@ -3721,8 +3721,21 @@ list_ass_subscript(PyObject* _self, PyObject* item, PyObject* value)

             selfitems = self->ob_item;
             seqitems = PySequence_Fast_ITEMS(seq);
+            // Added bounds and NULL checks before assignment
+            if (!selfitems || !seqitems || !garbage) {
+                Py_DECREF(seq);
+                if (garbage) PyMem_Free(garbage);
+                PyErr_SetString(PyExc_RuntimeError, "Null pointer encountered in slice assignment");
+                return -1;
+            }
             for (cur = start, i = 0; i < slicelength;
                  cur += (size_t)step, i++) {
+                if (cur >= (size_t)Py_SIZE(self)) {
+                    Py_DECREF(seq);
+                    PyMem_Free(garbage);
+                    PyErr_SetString(PyExc_IndexError, "list index out of range in slice assignment");
+                    return -1;
+                }
                 garbage[i] = selfitems[cur];
                 ins = Py_NewRef(seqitems[i]);
                 selfitems[cur] = ins;
