patch:
diff --git a/Modules/readline.c b/Modules/readline.c
index 35655c70a46..cf053d61a5e 100644
--- a/Modules/readline.c
+++ b/Modules/readline.c
@@ -362,13 +362,30 @@ readline_append_history_file_impl(PyObject *module, int nelements,
         filename_bytes = NULL;
         filename = NULL;
     }
+    /* Validate nelements to prevent invalid history access */
+    if (nelements < 0) {
+        Py_XDECREF(filename_bytes);
+        PyErr_SetString(PyExc_ValueError, "nelements must be non-negative");
+        return NULL;
+    }
+    /* Store errno to restore later and avoid potential race conditions */
+    int saved_errno = errno;
     errno = err = append_history(
         nelements - libedit_append_replace_history_offset, filename);
-    if (!err && _history_length >= 0)
-        history_truncate_file(filename, _history_length);
+    if (!err && _history_length >= 0) {
+        /* Verify history state is still valid before truncating */
+        if (history_get_history_state() != NULL) {
+            history_truncate_file(filename, _history_length);
+        }
+    }
     Py_XDECREF(filename_bytes);
-    errno = err;
-    if (errno)
+    /* Only update errno if an actual error occurred */
+    if (err != 0) {
+        errno = err;
+    } else {
+        errno = saved_errno;
+    }
+    if (errno && err != 0)
         return PyErr_SetFromErrno(PyExc_OSError);
     Py_RETURN_NONE;
 }
