patch:
diff --git a/Include/refcount.h b/Include/refcount.h
index 9a4e15065ec..9841b973680 100644
--- a/Include/refcount.h
+++ b/Include/refcount.h
@@ -477,7 +477,6 @@ static inline void Py_XDECREF(PyObject *op)
 #if !defined(Py_LIMITED_API) || Py_LIMITED_API+0 < 0x030b0000
 #  define Py_XDECREF(op) Py_XDECREF(_PyObject_CAST(op))
 #endif
-
 // Create a new strong reference to an object:
 // increment the reference count of the object and return the object.
 PyAPI_FUNC(PyObject*) Py_NewRef(PyObject *obj);
@@ -485,8 +484,12 @@ PyAPI_FUNC(PyObject*) Py_NewRef(PyObject *obj);
 // Similar to Py_NewRef(), but the object can be NULL.
 PyAPI_FUNC(PyObject*) Py_XNewRef(PyObject *obj);

-static inline PyObject* _Py_NewRef(PyObject *obj)
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+ static inline PyObject* _Py_NewRef(PyObject *obj)
 {
+    if (obj == NULL) {
+        return NULL;
+    }
     Py_INCREF(obj);
     return obj;
 }
@@ -497,8 +500,7 @@ static inline PyObject* _Py_XNewRef(PyObject *obj)
     return obj;
 }

-// Py_NewRef() and Py_XNewRef() are exported as functions for the stable ABI.
-// Names overridden with macros by static inline functions for best
+// Py_NewRef() and Py_XNewRef() are exported as functions for the stable ABI.// Names overridden with macros by static inline functions for best
 // performances.
 #if !defined(Py_LIMITED_API) || Py_LIMITED_API+0 < 0x030b0000
 #  define Py_NewRef(obj) _Py_NewRef(_PyObject_CAST(obj))
