patch:
diff --git a/Objects/frameobject.c b/Objects/frameobject.c
index 9f1c031dcb9..23ae5b7a2f5 100644
--- a/Objects/frameobject.c
+++ b/Objects/frameobject.c
@@ -315,8 +315,21 @@ framelocalsproxy_new(PyTypeObject *type, PyObject *args, PyObject *kwds)
         return NULL;
     }

+    // Add null check for args parameter
+    if (args == NULL || PyTuple_Size(args) < 1) {
+        Py_DECREF(self);
+        PyErr_SetString(PyExc_TypeError, "framelocalsproxy_new expected at least 1 argument");
+        return NULL;
+    }
+
     PyFrameObject *frame = (PyFrameObject*)PyTuple_GET_ITEM(args, 0);
-    assert(PyFrame_Check(frame));
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+    if (frame == NULL) {
+        Py_DECREF(self);
+        PyErr_SetString(PyExc_TypeError, "frame argument cannot be NULL");
+        return NULL;
+    }
+     assert(PyFrame_Check(frame));

     ((PyFrameLocalsProxyObject*)self)->frame = (PyFrameObject*)Py_NewRef(frame);
