patch:
diff --git a/Modules/_cursesmodule.c b/Modules/_cursesmodule.c
index ece6b13c788..1af33f9a0f4 100644
--- a/Modules/_cursesmodule.c
+++ b/Modules/_cursesmodule.c
@@ -3364,10 +3364,16 @@ _curses_initscr_impl(PyObject *module)
 /*[clinic end generated code: output=619fb68443810b7b input=514f4bce1821f6b5]*/
 {
     WINDOW *win;
-
+    
     if (curses_initscr_called) {
-        wrefresh(stdscr);
-        return (PyObject *)PyCursesWindow_New(stdscr, NULL);
+        if (stdscr != NULL && getmaxy(stdscr) > 0 && getmaxx(stdscr) > 0) {
+            wrefresh(stdscr);
+            return (PyObject *)PyCursesWindow_New(stdscr, NULL);
+        } else {
+            _cursesmodule_state *state = get_cursesmodule_state(module);
+            PyErr_SetString(state->error, "terminal dimensions not properly initialized");
+            return NULL;
+        }
     }

     win = initscr();
