patch:
diff --git a/Modules/_cursesmodule.c b/Modules/_cursesmodule.c
index 743b9e37fbc..bdcb2ff39d6 100644
--- a/Modules/_cursesmodule.c
+++ b/Modules/_cursesmodule.c
@@ -3253,10 +3253,16 @@ _curses_initscr_impl(PyObject *module)
 {
     WINDOW *win;
     PyCursesWindowObject *winobj;
-
+// FIXME: Crash CWE-908: Use of Uninitialized Resource
+ 
     if (initialised) {
-        wrefresh(stdscr);
-        return (PyObject *)PyCursesWindow_New(stdscr, NULL);
+        if (stdscr != NULL && getmaxy(stdscr) > 0 && getmaxx(stdscr) > 0) {
+            if (wrefresh(stdscr) == OK) {
+                return (PyObject *)PyCursesWindow_New(stdscr, NULL);
+            }
+        }
+        PyErr_SetString(PyCursesError, "stdscr not properly initialized");
+        return NULL;
     }

     win = initscr();
@@ -3264,9 +3270,7 @@ _curses_initscr_impl(PyObject *module)
     if (win == NULL) {
         PyErr_SetString(PyCursesError, catchall_NULL);
         return NULL;
-    }
-
-    initialised = initialised_setupterm = TRUE;
+    }    initialised = initialised_setupterm = TRUE;

 /* This was moved from initcurses() because it core dumped on SGI,
    where they're not defined until you've called initscr() */
