patch:
diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 18dc2859357..93b11961696 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -291,6 +291,15 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
 {
     PyObject *handle;

+    // Ensure strong references to prevent deallocation during function call
+    Py_INCREF(func);
+    if (arg != NULL) {
+        Py_INCREF(arg);
+    }
+    if (ctx != NULL) {
+        Py_INCREF(ctx);
+    }
+
     if (ctx == NULL) {
         PyObject *stack[] = {loop, func, arg};
         size_t nargsf = 3 | PY_VECTORCALL_ARGUMENTS_OFFSET;
@@ -312,6 +321,15 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
                                            state->context_kwname);
     }

+    // Release the strong references
+    Py_DECREF(func);
+    if (arg != NULL) {
+        Py_DECREF(arg);
+    }
+    if (ctx != NULL) {
+        Py_DECREF(ctx);
+    }
+
     if (handle == NULL) {
         return -1;
     }
