diff --git a/Modules/arraymodule.c b/Modules/arraymodule.c
index b80c964f20d..6abbc0d7c24 100644
--- a/Modules/arraymodule.c
+++ b/Modules/arraymodule.c
@@ -3087,10 +3087,16 @@ static PyObject *
 array_arrayiterator___setstate__(arrayiterobject *self, PyObject *state)
 /*[clinic end generated code: output=397da9904e443cbe input=f47d5ceda19e787b]*/
 {
+    if (self == NULL || self->ao == NULL) {
+        PyErr_SetString(PyExc_SystemError, "invalid array iterator state");
+        return NULL;
+    }
+    
     Py_ssize_t index = PyLong_AsSsize_t(state);
     if (index == -1 && PyErr_Occurred())
         return NULL;
-    if (index < 0)
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+     if (index < 0)
         index = 0;
     else if (index > Py_SIZE(self->ao))
         index = Py_SIZE(self->ao); /* iterator exhausted */
