diff --git a/Modules/arraymodule.c b/Modules/arraymodule.c
index b80c964f20d..5f08f5b8b8d 100644
--- a/Modules/arraymodule.c
+++ b/Modules/arraymodule.c
@@ -3090,6 +3090,11 @@ array_arrayiterator___setstate__(arrayiterobject *self, PyObject *state)
     Py_ssize_t index = PyLong_AsSsize_t(state);
     if (index == -1 && PyErr_Occurred())
         return NULL;
+// FIXME: Crash CWE-824: Access of Uninitialized Pointer
+    if (self->ao == NULL) {
+        PyErr_SetString(PyExc_RuntimeError, "arrayiterator: underlying array object is uninitialized");
+        return NULL;
+    }
     if (index < 0)
         index = 0;
     else if (index > Py_SIZE(self->ao))
