patch:
diff --git a/Modules/_ctypes/callproc.c b/Modules/_ctypes/callproc.c
index 352487ed964..db807ed312a 100644
--- a/Modules/_ctypes/callproc.c
+++ b/Modules/_ctypes/callproc.c
@@ -1024,8 +1024,9 @@ static PyObject *GetResult(ctypes_state *st,
         /* If restype is py_object (detected by comparing getfunc with
            O_get), we have to call Py_DECREF because O_get has already
            called Py_INCREF.
-        */
-        if (info->getfunc == _ctypes_get_fielddesc("O")->getfunc) {
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+         */
+        if (info->getfunc == _ctypes_get_fielddesc("O")->getfunc && retval != NULL) {
             Py_DECREF(retval);
         }
     }
