+         */
         if (info->getfunc == _ctypes_get_fielddesc("O")->getfunc) {
-            Py_DECREF(retval);
+            if (retval != NULL)
+                Py_DECREF(retval);
         }
     }
     else {
@@ -1042,6 +1044,7 @@ static PyObject *GetResult(ctypes_state *st,
     return v;
 }

